import { useContext } from "react";

import MealItemForm from "./MealItemForm";
import classes from "./MealItem.module.css";
import CartContext from "../../../store/cart-context";
import pic from "../../../assets/meals.jpg";

const MealItem = (props) => {
  const cartCtx = useContext(CartContext);

  const price = `$${props.price.toFixed(2)}`;

  const addToCartHandler = (amount) => {
    cartCtx.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price,
    });
  };


  // <div class="container">
  //     <div class="image">
  //       <img src="https://i.pinimg.com/originals/26/ea/fc/26eafc0b14488fea03fa8fa9751203ff.jpg">
  //     </div>
  //     <div class="text">
  //       <h1>Paris is one of the most beautiful cities in France.</h1>
  //     </div>
  //   </div>

  return (
    <li className={classes.meal}>
      <div className={classes.con}>
        <div className="image">
          <img src={pic} width="193" height="130" />
        </div>
        <div className="text">
          <h3>{props.name}</h3>
        {/* <div className={classes.description}>{props.description}</div>
            <div className={classes.price}>{price}</div> */}
        </div>
        </div>
      <div>
        <MealItemForm id={props.id} onAddToCart={addToCartHandler} />
      </div>
    </li>
  );
};

export default MealItem;
