import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { Button } from 'antd';
import 'antd/dist/antd.css';

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
    type: "Japanese"
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A Tailand specialty!",
    price: 16.5,
    type: "Tailand"
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
    type: "Chinese"
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese"
  },
];

let mealsList;

const handleAll=()=>{
  mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
}

const handleChi=()=>{
  mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
}

const handleJap=()=>{

  mealsList = DUMMY_MEALS.filter(meal => 
     meal.type === "Japanese" 
  );
  mealsList = mealsList.map( meal =>
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  )
  console.log(mealsList)
}

function handleTai() {
  mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price} />
  ));
}

const AvailableMeals = () => {
  mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  useEffect(() => {
    if (items.length === 0) {
      return;
    }
    setBtnIsHighlighted(true);

    const timer = setTimeout(() => {
      setBtnIsHighlighted(false);
    }, 300);

    return () => {
      clearTimeout(timer);
    };
  }, [items]);

  return (
    <section className={classes.meals}>
      <div className="site-button-ghost-wrapper">
        <Button type="ghost" danger ghost onClick={handleAll}>
          All
        </Button>
        <Button type="primary" danger ghost onClick={handleJap}>
          Japanese
        </Button>
        <Button type="primary" danger ghost onClick={handleChi}>
          Chinese
        </Button>
        <Button type="primary" danger ghost onClick={handleTai}>
          Tailand
        </Button>
      </div>
      ,
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
