import { useState } from 'react';

import Header from './components/Layout/Header';
import Meals from './components/Meals/Meals';
import Cart from './components/Cart/Cart';
import CartProvider from './store/CartProvider';

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const showAbout =()=>{
    setShowAbout(true)
    setShowMenu(false)
    setShowContact(false)
  }

  const showMenu =()=>{
    setShowMenu(true)
    setShowAbout(false)
    setShowContact(false)
  }


  const showContact =()=>{
    setShowContact(true)
    setShowContact(false)
    setShowAbout(false)
  }



  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      <Header onShowCart={showCartHandler} showAbout={showAbout} showContact={showContact} showMeal={showMenu} />
      <main>
        <Meals showAbout={showAbout} showMeal={showMeal} showContact = {showContact}/>
      </main>
    </CartProvider>
  );
}

export default App;
