import { Fragment } from "react";

import HeaderCartButton from "./HeaderCartButton";
import mealsImage from "../../assets/panda.jpeg";
import classes from "./Header.module.css";
import { Button } from "antd";
import "antd/dist/antd.css";

const Header = (props) => {
  return (
    <Fragment>
      <header className={classes.header}>
        <h1 className={classes.h1}>Asian Food Market</h1>

        <button className={btnClasses} onClick={props.onClick}>
          <span className={classes.icon}>
          </span>
          <span>Your Cart</span>
          <span className={classes.badge}>{numberOfCartItems}</span>
        </button>

        <Button type="Dashed" ghost onClick={props.onshowAbout}>
          About us
        </Button>

        <Button type="Dashed" ghost onClick={props.onshowMenu}>
          Menu
        </Button>

        <Button type="Dashed" ghost onClick={props.onshowContact}>
          Contact us
        </Button>

        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImage} alt="kongfu-pandas" />
      </div>
    </Fragment>
  );
};

export default Header;
