import { Fragment } from "react";
import HeaderCartButton from "./HeaderCartButton";
import mealsImage from "../../assets/panda.jpeg";
import classes from "./Header.module.css";
import 'antd/dist/antd.css';
import { MenuOutlined, InfoCircleFilled, PhoneFilled} from '@ant-design/icons';


const Header = (props) => {
  const [style1, setStyle1] = useState("classes.button")
  const [style2, setStyle2] = useState("classes.button")
  const [style3, setStyle3] = useState("classes.button")

  useEffect(() => {
    // console.log("effect")
  }, [style1, style2, style3]);

function handleAbout(){
  props.onshowAbout
  setStyle1("")
  setStyle2("classes.button")
  setStyle3("classes.button")
}

function handleMenu(){
  props.onshowMenu
  setStyle1("classes.button")
  setStyle2("")
  setStyle3("classes.button")
}

function HandleContact(){
  props.onshowContact
  setStyle1("classes.button")
  setStyle2("")
  setStyle3("classes.button")
}

  return (
    <Fragment>
      <header className={classes.header}>
        <h1 className={classes.h1}>Pandas Grocery</h1>
        <button className={style1} onClick={handleAbout}>
        {/* <RightCircleFilled />&nbsp; */}
        <InfoCircleFilled />&nbsp;
          <span>About us</span>
        </button>

        <button className={style2} onClick={props.onshowMenu}>
        <MenuOutlined />&nbsp;
          <span>Menu</span>
        </button>

        <button className={style3} onClick={props.onshowContact}>
        <PhoneFilled /> &nbsp;
          <span>Contact us</span>
        </button>

        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImage} alt="kongfu-pandas" />
      </div>
    </Fragment>
  );
};

export default Header;
