import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { Button } from 'antd';
import 'antd/dist/antd.css';
import { useEffect, useState } from 'react';

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A Tailand specialty!",
    price: 16.5,
    type: "Tailand",
    picture: "/meals.jpg"
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
    type: "Chinese",
    picture: "/meals.jpg"
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m5",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m6",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m7",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m8",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m9",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
  {
    id: "m10",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    type: "Japanese",
    picture: "/meals.jpg"
  },
];




const AvailableMeals = () => {

  let mealsList = DUMMY_MEALS.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  const [changedMeal, setMeal] = useState(mealsList);
  const handleAll=()=>{
    setMeal(mealsList)
  }

  const handleChi=()=>{
    HandleSelection("Chinese")
  }

  const handleJap=()=>{
    HandleSelection("Japanese")
  }

  function handleTai() {
    HandleSelection("Tailand")
  }

  useEffect(() => {
  }, [changedMeal]);

  function HandleSelection(country){
    let temp = DUMMY_MEALS.filter(meal => 
      meal.type === country 
   );
   temp = temp.map( meal =>
     <MealItem
       key={meal.id}
       id={meal.id}
       name={meal.name}
       description={meal.description}
       price={meal.price}
       picture ={meal.picture}
     />
   )
   setMeal(temp)

  }

  return (

    <section className={classes.meals}>
      <div className="site-button-ghost-wrapper">
        <Button type="ghost" danger ghost onClick={handleAll}>
          All
        </Button>
        <Button type="primary" danger ghost onClick={handleJap}>
          Japanese
        </Button>
        <Button type="primary" danger ghost onClick={handleChi}>
          Chinese
        </Button>
        <Button type="primary" danger ghost onClick={handleTai}>
          Tailand
        </Button>
      </div>
      ,
      <Card>
        <ul>{changedMeal}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
